/**
 * Header Component with navigation and accessibility controls
 */

import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

interface HeaderProps {
  onToggleContrast: () => void;
  highContrast: boolean;
}

const Header: React.FC<HeaderProps> = ({ onToggleContrast, highContrast }) => {
  const { t, i18n } = useTranslation();
  const location = useLocation();
  const [menuOpen, setMenuOpen] = useState(false);

  const changeLanguage = (lang: string) => {
    i18n.changeLanguage(lang);
    localStorage.setItem('krishi-lang', lang);
  };

  const isActive = (path: string) => location.pathname === path;

  return (
    <header className="bg-white shadow-sm sticky top-0 z-40">
      {/* Skip link for accessibility */}
      <a href="#main" className="skip-link">
        {t('accessibility.skipToMain')}
      </a>

      <div className="max-w-7xl mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <Link to="/" className="flex items-center gap-2">
            <span className="text-2xl">üåæ</span>
            <span className="font-bold text-xl text-primary-700">
              {t('app.name')}
            </span>
          </Link>

          {/* Desktop Navigation */}
          <nav className="hidden md:flex items-center gap-6">
            <NavLink to="/" active={isActive('/')}>
              {t('nav.home')}
            </NavLink>
            <NavLink to="/profile" active={isActive('/profile')}>
              {t('nav.profile')}
            </NavLink>
            <NavLink to="/schemes" active={isActive('/schemes')}>
              {t('nav.schemes')}
            </NavLink>
            <NavLink to="/documents" active={isActive('/documents')}>
              {t('nav.documents')}
            </NavLink>
            <NavLink to="/help" active={isActive('/help')}>
              {t('nav.help')}
            </NavLink>
          </nav>

          {/* Right Controls */}
          <div className="flex items-center gap-4">
            {/* Language Selector */}
            <div className="relative">
              <select
                value={i18n.language}
                onChange={(e) => changeLanguage(e.target.value)}
                className="appearance-none bg-transparent border border-gray-300 rounded-lg px-3 py-1.5 pr-8 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                aria-label={t('nav.language')}
              >
                <option value="en">English</option>
                <option value="hi">‡§π‡§ø‡§Ç‡§¶‡•Ä</option>
                <option value="mr">‡§Æ‡§∞‡§æ‡§†‡•Ä</option>
              </select>
              <span className="absolute right-2 top-1/2 -translate-y-1/2 pointer-events-none">
                üåê
              </span>
            </div>

            {/* High Contrast Toggle */}
            <button
              onClick={onToggleContrast}
              className={`p-2 rounded-lg transition-colors ${
                highContrast 
                  ? 'bg-yellow-400 text-black' 
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
              aria-label={t('accessibility.highContrast')}
              title={t('accessibility.highContrast')}
            >
              üëÅÔ∏è
            </button>

            {/* Mobile Menu Button */}
            <button
              className="md:hidden p-2"
              onClick={() => setMenuOpen(!menuOpen)}
              aria-label="Menu"
            >
              {menuOpen ? '‚úï' : '‚ò∞'}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {menuOpen && (
          <nav className="md:hidden py-4 border-t">
            <div className="flex flex-col gap-2">
              <MobileNavLink to="/" onClick={() => setMenuOpen(false)}>
                {t('nav.home')}
              </MobileNavLink>
              <MobileNavLink to="/profile" onClick={() => setMenuOpen(false)}>
                {t('nav.profile')}
              </MobileNavLink>
              <MobileNavLink to="/schemes" onClick={() => setMenuOpen(false)}>
                {t('nav.schemes')}
              </MobileNavLink>
              <MobileNavLink to="/documents" onClick={() => setMenuOpen(false)}>
                {t('nav.documents')}
              </MobileNavLink>
              <MobileNavLink to="/help" onClick={() => setMenuOpen(false)}>
                {t('nav.help')}
              </MobileNavLink>
            </div>
          </nav>
        )}
      </div>
    </header>
  );
};

const NavLink: React.FC<{
  to: string;
  active: boolean;
  children: React.ReactNode;
}> = ({ to, active, children }) => (
  <Link
    to={to}
    className={`text-sm font-medium transition-colors ${
      active
        ? 'text-primary-600'
        : 'text-gray-600 hover:text-gray-900'
    }`}
  >
    {children}
  </Link>
);

const MobileNavLink: React.FC<{
  to: string;
  onClick: () => void;
  children: React.ReactNode;
}> = ({ to, onClick, children }) => (
  <Link
    to={to}
    onClick={onClick}
    className="px-4 py-2 text-gray-700 hover:bg-gray-100 rounded-lg"
  >
    {children}
  </Link>
);

export default Header;
