/**
 * Landing Page Component
 */

import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

const Landing: React.FC = () => {
  const { t } = useTranslation();
  const navigate = useNavigate();

  const features = [
    {
      icon: 'üé§',
      title: t('landing.features.voice'),
      desc: t('landing.features.voiceDesc')
    },
    {
      icon: 'üì∂',
      title: t('landing.features.offline'),
      desc: t('landing.features.offlineDesc')
    },
    {
      icon: 'üîí',
      title: t('landing.features.secure'),
      desc: t('landing.features.secureDesc')
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-primary-50 to-white">
      {/* Hero Section */}
      <section className="pt-16 pb-24 px-4">
        <div className="max-w-4xl mx-auto text-center">
          <div className="mb-8">
            <span className="text-6xl">üåæ</span>
          </div>
          <h1 className="text-4xl md:text-5xl font-bold text-gray-900 mb-4">
            {t('landing.welcome')}
          </h1>
          <p className="text-xl text-gray-600 mb-8">
            {t('landing.subtitle')}
          </p>
          <button
            onClick={() => navigate('/profile')}
            className="btn-primary text-lg px-8 py-4"
            aria-label={t('landing.getStarted')}
          >
            {t('landing.getStarted')} ‚Üí
          </button>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-16 px-4 bg-white">
        <div className="max-w-4xl mx-auto">
          <div className="grid md:grid-cols-3 gap-8">
            {features.map((feature, index) => (
              <div
                key={index}
                className="card text-center animate-slide-up"
                style={{ animationDelay: `${index * 100}ms` }}
              >
                <span className="text-4xl mb-4 block">{feature.icon}</span>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">
                  {feature.title}
                </h3>
                <p className="text-gray-600">{feature.desc}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* How it works */}
      <section className="py-16 px-4">
        <div className="max-w-4xl mx-auto">
          <h2 className="text-2xl font-bold text-center mb-12">
            How It Works
          </h2>
          <div className="flex flex-col md:flex-row items-center justify-between gap-8">
            <Step number={1} title="Create Profile" icon="üìù" />
            <Arrow />
            <Step number={2} title="Get Matched" icon="üéØ" />
            <Arrow />
            <Step number={3} title="Apply" icon="‚úÖ" />
          </div>
        </div>
      </section>

      {/* Privacy Note */}
      <section className="py-8 px-4 bg-primary-50">
        <div className="max-w-2xl mx-auto text-center">
          <p className="text-sm text-gray-600">
            üîê {t('profile.privacyNote')}
          </p>
        </div>
      </section>
    </div>
  );
};

const Step: React.FC<{ number: number; title: string; icon: string }> = ({
  number,
  title,
  icon
}) => (
  <div className="flex flex-col items-center">
    <div className="w-16 h-16 rounded-full bg-primary-100 flex items-center justify-center mb-3">
      <span className="text-2xl">{icon}</span>
    </div>
    <span className="text-sm text-primary-600 font-medium">Step {number}</span>
    <span className="font-semibold text-gray-900">{title}</span>
  </div>
);

const Arrow: React.FC = () => (
  <span className="hidden md:block text-3xl text-gray-300">‚Üí</span>
);

export default Landing;
