/**
 * Admin Dashboard Page ‚Äî Metrics, application overview, charts
 */

import React, { useEffect, useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import LoadingSpinner from '../components/LoadingSpinner';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:4001/api/v1';

interface Metrics {
    overview: {
        total_users: number;
        total_profiles: number;
        total_applications: number;
        total_documents: number;
        recent_applications_7d: number;
        avg_farmer_income: number;
    };
    applications_by_status: Record<string, number>;
    top_states: Array<{ state: string; count: number }>;
    top_schemes: Array<{ scheme: string; count: number }>;
}

const AdminDashboard: React.FC = () => {
    const { token } = useAuth();
    const [metrics, setMetrics] = useState<Metrics | null>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchMetrics = async () => {
            if (!token) {
                setError('Please login to access admin dashboard');
                setLoading(false);
                return;
            }
            try {
                const response = await fetch(`${API_URL}/admin/metrics`, {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (!response.ok) throw new Error('Failed to fetch metrics');
                const data = await response.json();
                setMetrics(data);
            } catch (err) {
                console.error(err);
                setError('Failed to load metrics');
                // Set demo data for offline
                setMetrics({
                    overview: {
                        total_users: 127,
                        total_profiles: 104,
                        total_applications: 89,
                        total_documents: 213,
                        recent_applications_7d: 23,
                        avg_farmer_income: 165000
                    },
                    applications_by_status: {
                        SUBMITTED: 34,
                        RECEIVED: 22,
                        UNDER_REVIEW: 18,
                        APPROVED: 12,
                        REJECTED: 3
                    },
                    top_states: [
                        { state: 'Maharashtra', count: 42 },
                        { state: 'Gujarat', count: 23 },
                        { state: 'Madhya Pradesh', count: 18 },
                        { state: 'Rajasthan', count: 12 },
                        { state: 'Karnataka', count: 9 }
                    ],
                    top_schemes: [
                        { scheme: 'PM-KISAN Samman Nidhi', count: 34 },
                        { scheme: 'PM Fasal Bima Yojana', count: 22 },
                        { scheme: 'Kisan Credit Card', count: 18 },
                        { scheme: 'Soil Health Card', count: 9 },
                        { scheme: 'PM Krishi Sinchai Yojana', count: 6 }
                    ]
                });
            } finally {
                setLoading(false);
            }
        };
        fetchMetrics();
    }, [token]);

    if (loading) return <LoadingSpinner />;

    if (!metrics) return (
        <div className="max-w-4xl mx-auto px-4 py-8">
            <p className="text-red-600">{error}</p>
        </div>
    );

    const statusColors: Record<string, string> = {
        SUBMITTED: 'bg-blue-100 text-blue-800',
        RECEIVED: 'bg-yellow-100 text-yellow-800',
        UNDER_REVIEW: 'bg-purple-100 text-purple-800',
        APPROVED: 'bg-green-100 text-green-800',
        REJECTED: 'bg-red-100 text-red-800'
    };

    return (
        <div className="max-w-6xl mx-auto px-4 py-8">
            <h1 className="text-2xl font-bold text-gray-900 mb-6">üìä Admin Dashboard</h1>

            {error && (
                <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3 mb-4 text-sm text-yellow-800">
                    ‚ö†Ô∏è Showing demo data ‚Äî {error}
                </div>
            )}

            {/* Overview Cards */}
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4 mb-8">
                {[
                    { label: 'Total Users', value: metrics.overview.total_users, icon: 'üë•' },
                    { label: 'Profiles Created', value: metrics.overview.total_profiles, icon: 'üìù' },
                    { label: 'Applications', value: metrics.overview.total_applications, icon: 'üìÑ' },
                    { label: 'Documents Uploaded', value: metrics.overview.total_documents, icon: 'üóÇÔ∏è' },
                    { label: 'Last 7 Days', value: metrics.overview.recent_applications_7d, icon: 'üìà' },
                    { label: 'Avg Income', value: `‚Çπ${metrics.overview.avg_farmer_income.toLocaleString('en-IN')}`, icon: 'üí∞' }
                ].map((card, i) => (
                    <div key={i} className="bg-white rounded-lg shadow p-4 border-l-4 border-primary-500">
                        <p className="text-sm text-gray-500">{card.icon} {card.label}</p>
                        <p className="text-2xl font-bold text-gray-900 mt-1">{card.value}</p>
                    </div>
                ))}
            </div>

            <div className="grid md:grid-cols-2 gap-6">
                {/* Applications by Status */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-lg font-semibold mb-4">Applications by Status</h2>
                    <div className="space-y-3">
                        {Object.entries(metrics.applications_by_status).map(([status, count]) => {
                            const total = metrics.overview.total_applications || 1;
                            const pct = Math.round((count / total) * 100);
                            return (
                                <div key={status}>
                                    <div className="flex justify-between items-center mb-1">
                                        <span className={`text-xs font-semibold px-2 py-1 rounded-full ${statusColors[status] || 'bg-gray-100 text-gray-800'}`}>
                                            {status}
                                        </span>
                                        <span className="text-sm text-gray-600">{count} ({pct}%)</span>
                                    </div>
                                    <div className="w-full bg-gray-100 rounded-full h-2">
                                        <div className="bg-primary-500 h-2 rounded-full" style={{ width: `${pct}%` }}></div>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </div>

                {/* Top States */}
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-lg font-semibold mb-4">Top States</h2>
                    <div className="space-y-3">
                        {metrics.top_states.map((s, i) => (
                            <div key={i} className="flex items-center justify-between">
                                <div className="flex items-center gap-2">
                                    <span className="text-sm font-medium w-6 text-center text-gray-400">#{i + 1}</span>
                                    <span className="text-sm text-gray-900">{s.state}</span>
                                </div>
                                <span className="text-sm font-semibold text-primary-600">{s.count} farmers</span>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Top Schemes */}
                <div className="bg-white rounded-lg shadow p-6 md:col-span-2">
                    <h2 className="text-lg font-semibold mb-4">Most Applied Schemes</h2>
                    <div className="grid md:grid-cols-2 gap-4">
                        {metrics.top_schemes.map((s, i) => (
                            <div key={i} className="flex items-center gap-3 p-3 bg-gray-50 rounded-lg">
                                <span className="text-2xl font-bold text-primary-500">#{i + 1}</span>
                                <div>
                                    <p className="text-sm font-medium text-gray-900">{s.scheme}</p>
                                    <p className="text-xs text-gray-500">{s.count} applications</p>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
