import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { useTranslation } from 'react-i18next';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:4000/api/v1';

const Login: React.FC = () => {
    const { t } = useTranslation();
    const navigate = useNavigate();
    const { login } = useAuth();

    const [mobile, setMobile] = useState('');
    const [otp, setOtp] = useState('');
    const [step, setStep] = useState<'mobile' | 'otp'>('mobile');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [demoOtp, setDemoOtp] = useState<string | null>(null);

    const handleRequestOtp = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        setError(null);
        try {
            const res = await axios.post(`${API_URL}/auth/request-otp`, { mobile });
            setStep('otp');
            if (res.data.demo_otp) {
                setDemoOtp(res.data.demo_otp);
                // alert(`Demo OTP: ${res.data.demo_otp}`);
            }
        } catch (err: any) {
            setError(err.response?.data?.errors?.[0]?.msg || 'Failed to request OTP');
        } finally {
            setLoading(false);
        }
    };

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        setError(null);
        try {
            const res = await axios.post(`${API_URL}/auth/login`, { mobile, otp });
            login(res.data.token, res.data.user);

            // Check if profile exists
            try {
                await axios.get(`${API_URL}/profile`, {
                    headers: { Authorization: `Bearer ${res.data.token}` }
                });
                navigate('/schemes'); // Profile exists, go to schemes
            } catch (err) {
                navigate('/profile'); // No profile, go to wizard
            }
        } catch (err: any) {
            setError(err.response?.data?.error || 'Login failed');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 px-4">
            <div className="max-w-md w-full bg-white rounded-lg shadow-lg p-8">
                <div className="text-center mb-8">
                    <span className="text-4xl">ðŸŒ¾</span>
                    <h2 className="text-2xl font-bold mt-4 text-gray-900">
                        {t('login.welcome') || 'Welcome to KRISHI-AI'}
                    </h2>
                    <p className="text-gray-600 mt-2">
                        {step === 'mobile'
                            ? (t('login.enterMobile') || 'Enter your mobile number to access schemes')
                            : (t('login.enterOtp') || 'Enter the OTP sent to your mobile')
                        }
                    </p>
                </div>

                {error && (
                    <div className="bg-red-50 text-red-700 p-3 rounded mb-4 text-sm">
                        {error}
                    </div>
                )}

                {demoOtp && step === 'otp' && (
                    <div className="bg-green-50 text-green-700 p-3 rounded mb-4 text-sm">
                        Demo OTP: <strong>{demoOtp}</strong>
                    </div>
                )}

                {step === 'mobile' ? (
                    <form onSubmit={handleRequestOtp} className="space-y-6">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Mobile Number
                            </label>
                            <div className="relative">
                                <span className="absolute left-3 top-3 text-gray-500">+91</span>
                                <input
                                    type="tel"
                                    value={mobile}
                                    onChange={(e) => setMobile(e.target.value.replace(/\D/g, '').slice(0, 10))}
                                    className="pl-12 w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 py-2.5 bg-gray-50 border"
                                    placeholder="9876543210"
                                    maxLength={10}
                                    required
                                />
                            </div>
                        </div>
                        <button
                            type="submit"
                            disabled={loading || mobile.length !== 10}
                            className={`w-full btn-primary py-3 ${loading ? 'opacity-70' : ''}`}
                        >
                            {loading ? 'Sending...' : 'Get OTP'}
                        </button>
                    </form>
                ) : (
                    <form onSubmit={handleLogin} className="space-y-6">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                OTP
                            </label>
                            <input
                                type="text"
                                value={otp}
                                onChange={(e) => setOtp(e.target.value.replace(/\D/g, '').slice(0, 6))}
                                className="w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500 py-2.5 bg-gray-50 border text-center text-lg tracking-widest"
                                placeholder="123456"
                                maxLength={6}
                                required
                            />
                        </div>
                        <button
                            type="submit"
                            disabled={loading || otp.length !== 6}
                            className={`w-full btn-primary py-3 ${loading ? 'opacity-70' : ''}`}
                        >
                            {loading ? 'Verifying...' : 'Login'}
                        </button>
                        <button
                            type="button"
                            onClick={() => setStep('mobile')}
                            className="w-full text-sm text-primary-600 hover:text-primary-700"
                        >
                            Change Mobile Number
                        </button>
                    </form>
                )}
            </div>
        </div>
    );
};

export default Login;
