/**
 * Offline Banner Component - Shows when user is offline
 */

import React from 'react';
import { useTranslation } from 'react-i18next';
import { useOffline } from '../contexts/OfflineContext';

const OfflineBanner: React.FC = () => {
  const { t } = useTranslation();
  const { isOffline, queuedCount } = useOffline();

  if (!isOffline) return null;

  return (
    <div 
      className="bg-[#F6A500] text-gray-900 px-4 py-3 text-center text-sm font-medium flex items-center justify-center flex-wrap gap-2"
      role="alert"
      aria-live="polite"
    >
      <span className="inline-flex items-center gap-1.5">
        <svg className="w-4 h-4 animate-pulse" fill="none" stroke="currentColor" viewBox="0 0 24 24" aria-hidden="true">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M18.364 5.636a9 9 0 010 12.728m0 0l-2.829-2.829m2.829 2.829L21 21M15.536 8.464a5 5 0 010 7.072m0 0l-2.829-2.829m-4.243 2.829a4.978 4.978 0 01-1.414-2.83m-1.414 5.658a9 9 0 01-2.167-9.238m7.824 2.167a1 1 0 111.414 1.414m-1.414-1.414L3 3m8.293 8.293l1.414 1.414" />
        </svg>
        <strong>{t('offline.banner') || 'Offline Mode Active'}</strong>
      </span>
      <span>{t('offline.bannerDesc') || 'Changes will sync when you\'re back online.'}</span>
      {queuedCount > 0 && (
        <span className="bg-white/90 text-gray-800 px-3 py-1 rounded-full text-xs font-bold">
          {queuedCount} {t('offline.queued') || 'pending'}
        </span>
      )}
    </div>
  );
};

export default OfflineBanner;
