/**
 * Offline Banner Component - Shows when user is offline
 */

import React from 'react';
import { useTranslation } from 'react-i18next';
import { useOffline } from '../contexts/OfflineContext';

const OfflineBanner: React.FC = () => {
  const { t } = useTranslation();
  const { isOffline, queuedCount } = useOffline();

  if (!isOffline) return null;

  return (
    <div 
      className="bg-yellow-500 text-yellow-900 px-4 py-2 text-center text-sm"
      role="alert"
    >
      <span className="mr-2">ðŸ“¶</span>
      <strong>{t('offline.banner')}</strong>
      <span className="mx-2">â€”</span>
      <span>{t('offline.bannerDesc')}</span>
      {queuedCount > 0 && (
        <span className="ml-2 bg-yellow-600 text-white px-2 py-0.5 rounded-full text-xs">
          {queuedCount} {t('offline.queued')}
        </span>
      )}
    </div>
  );
};

export default OfflineBanner;
