import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import LoadingSpinner from '../components/LoadingSpinner';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:4000/api/v1';

const ApplicationForm: React.FC = () => {
  const { schemeId } = useParams<{ schemeId: string }>();
  const { t } = useTranslation();
  const navigate = useNavigate();
  const location = useLocation();
  const { token, login, user } = useAuth();

  const [schemeName, setSchemeName] = useState<string>(location.state?.schemeName || '');
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Profile state
  const [profile, setProfile] = useState<any>(null);

  // Documents state
  const [requiredDocs, setRequiredDocs] = useState<string[]>([]);
  const [uploadedDocs, setUploadedDocs] = useState<Record<string, boolean>>({});

  useEffect(() => {
    // 1. Auto-login for demo if needed
    const ensureAuth = async () => {
      if (!token) {
        try {
          // Demo login
          const res = await axios.post(`${API_URL}/auth/login`, {
            mobile: '9876543210',
            otp: '1234' // Mock OTP
          });
          login(res.data.token, res.data.user);
        } catch (err) {
          console.error('Demo auth failed', err);
          setError('Authentication failed. Please try again.');
        }
      }
    };

    ensureAuth();
  }, [token, login]);

  useEffect(() => {
    // 2. Load Profile and Scheme Details
    const loadData = async () => {
      setLoading(true);
      try {
        // Load Profile
        const savedProfile = localStorage.getItem('krishi-profile');
        if (savedProfile) {
          setProfile(JSON.parse(savedProfile));
        } else {
          // If no local profile, try fetching from backend if user exists
          // For now redirect to profile wizard if absolutely no profile
          if (!user) {
            navigate('/profile');
            return;
          }
        }

        // Load Scheme Details (to get name and required docs)
        if (schemeId) {
          const res = await axios.get(`${API_URL}/schemes/${schemeId}`);
          setSchemeName(res.data.name);
          setRequiredDocs(res.data.documents_required || ['Aadhaar Card', 'Bank Passbook']);
        }
      } catch (err) {
        console.error('Error loading data', err);
        setError('Failed to load application details');
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [schemeId, navigate, user]);

  const handleFileUpload = (docName: string) => {
    // Simulate upload
    setUploadedDocs(prev => ({ ...prev, [docName]: true }));
  };

  const handleSubmit = async () => {
    if (!profile?.profile_id) {
      setError("Profile ID missing. Please update your profile.");
      return;
    }

    setSubmitting(true);
    setError(null);

    try {
      const payload = {
        profile_id: profile.profile_id,
        scheme_id: schemeId,
        scheme_name: schemeName,
        documents: Object.keys(uploadedDocs), // List of verified docs
        form_data: {
          ...profile,
          notes: "Applied via KRISHI-AI Portal"
        },
        client_timestamp: new Date().toISOString()
      };

      const res = await axios.post(
        `${API_URL}/application/submit`,
        payload,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Success
      navigate(`/status/${res.data.application_id}`);

    } catch (err) {
      console.error('Submission failed', err);
      setError(t('common.error') || 'Submission failed');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="max-w-2xl mx-auto px-4 py-8">
      <h1 className="text-2xl font-bold mb-6">{t('application.title') || 'Application Form'}</h1>

      {error && (
        <div className="p-4 mb-4 text-red-700 bg-red-100 rounded-lg border border-red-200">
          {error}
        </div>
      )}

      <div className="bg-white rounded-lg shadow p-6 mb-6">
        <h2 className="text-xl font-semibold mb-4 text-primary-700">
          {schemeName}
        </h2>

        {/* Profile Summary */}
        <div className="mb-6 pb-6 border-b">
          <h3 className="text-lg font-medium mb-3">Applicant Details</h3>
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <p className="text-gray-500">Name</p>
              <p className="font-medium">{profile?.name}</p>
            </div>
            <div>
              <p className="text-gray-500">Mobile</p>
              <p className="font-medium">{profile?.mobile}</p>
            </div>
            <div>
              <p className="text-gray-500">District</p>
              <p className="font-medium">{profile?.district}</p>
            </div>
          </div>
        </div>

        {/* Document Checklist */}
        <div className="mb-8">
          <h3 className="text-lg font-medium mb-3">Required Documents</h3>
          <div className="space-y-3">
            {requiredDocs.map((doc, idx) => (
              <div key={idx} className="flex items-center justify-between p-3 bg-gray-50 rounded border">
                <span className="flex items-center gap-2">
                  <span className="text-gray-500">ðŸ“„</span>
                  {doc}
                </span>

                {uploadedDocs[doc] ? (
                  <span className="text-green-600 font-medium flex items-center gap-1">
                    âœ“ Uploaded
                  </span>
                ) : (
                  <button
                    onClick={() => handleFileUpload(doc)}
                    className="text-primary-600 text-sm hover:underline"
                  >
                    Upload/Verify
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>

        <button
          onClick={handleSubmit}
          disabled={submitting}
          className={`w-full btn-primary py-3 text-lg ${submitting ? 'opacity-70 cursor-not-allowed' : ''}`}
        >
          {submitting ? t('application.submitting') : 'Submit Application'}
        </button>
      </div>
    </div>
  );
};

export default ApplicationForm;
