/**
 * Profile Wizard Page - Multi-step form for farmer profile creation
 */

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import VoiceInput from '../components/VoiceInput';

interface ProfileData {
  name: string;
  mobile: string;
  state: string;
  district: string;
  village: string;
  landType: 'irrigated' | 'dry' | 'mixed';
  acreage: number;
  mainCrops: string[];
  familyCount: number;
  annualIncome: number;
  farmerType: 'owner' | 'tenant' | 'sharecropper';
}

const STATES = [
  'Maharashtra', 'Gujarat', 'Madhya Pradesh', 'Rajasthan', 'Karnataka',
  'Andhra Pradesh', 'Telangana', 'Tamil Nadu', 'Uttar Pradesh', 'Punjab',
  'Haryana', 'Bihar', 'West Bengal', 'Odisha', 'Chhattisgarh'
];

const CROPS = [
  'rice', 'wheat', 'cotton', 'sugarcane', 'pulses', 'oilseeds',
  'maize', 'soybean', 'vegetables', 'fruits', 'mango', 'banana'
];

const ProfileWizard: React.FC = () => {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const [profile, setProfile] = useState<ProfileData>({
    name: '',
    mobile: '',
    state: '',
    district: '',
    village: '',
    landType: 'irrigated',
    acreage: 0,
    mainCrops: [],
    familyCount: 1,
    annualIncome: 0,
    farmerType: 'owner'
  });

  const updateProfile = (field: keyof ProfileData, value: unknown) => {
    setProfile(prev => ({ ...prev, [field]: value }));
  };

  const handleVoiceResult = (text: string, field: keyof ProfileData) => {
    updateProfile(field, text);
  };

  const handleNext = () => {
    if (step < 3) setStep(step + 1);
  };

  const handleBack = () => {
    if (step > 1) setStep(step - 1);
  };

  const handleSubmit = async () => {
    setIsSubmitting(true);

    try {
      // 1. Submit to backend
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:4000/api/v1';
      // For demo purposes, we might need a mock login to get a token, 
      // but assuming we are authenticated or the endpoint allows it for this prototype.
      // NOTE: backend requires auth, so we should probably handle that.
      // For now, let's assume the user is "logged in" or we skip auth for the prototype if possible.
      // But the backend middleware enforces 'authenticate'. 
      // We will handle auth in a real app. For prototype, we might rely on a pre-set token or mock login.

      // Attempt to save to backend (if authenticated)
      // If not authenticated, we just store locally for demo flows that don't strictly require backend persistence yet
      // However, ApplicationForm needs profile_id. 

      // Let's try to save if we have a token, otherwise just local.
      // But we need a profile_id. Let's generate a temporary one if backend fails or offline.

      const token = localStorage.getItem('krishi-auth-token');
      let profileId = `local-${Date.now()}`;

      if (token) {
        const response = await fetch(`${API_URL}/profile`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify(profile)
        });

        if (response.ok) {
          const data = await response.json();
          profileId = data.profile.profile_id;
        }
      }

      // Store profile locally with ID
      const profileWithId = { ...profile, profile_id: profileId };
      localStorage.setItem('krishi-profile', JSON.stringify(profileWithId));

      // Navigate to schemes with profile data
      navigate('/schemes', { state: { profile: profileWithId } });

    } catch (error) {
      console.error("Failed to save profile remotely", error);
      // Fallback: save locally
      const profileId = `local-${Date.now()}`;
      const profileWithId = { ...profile, profile_id: profileId };
      localStorage.setItem('krishi-profile', JSON.stringify(profileWithId));
      navigate('/schemes', { state: { profile: profileWithId } });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCropToggle = (crop: string) => {
    const crops = profile.mainCrops.includes(crop)
      ? profile.mainCrops.filter(c => c !== crop)
      : [...profile.mainCrops, crop];
    updateProfile('mainCrops', crops);
  };

  return (
    <div className="max-w-2xl mx-auto px-4 py-8">
      <h1 className="text-2xl font-bold text-gray-900 mb-2">
        {t('profile.title')}
      </h1>

      {/* Progress Steps */}
      <div className="flex items-center gap-2 mb-8">
        {[1, 2, 3].map(s => (
          <div
            key={s}
            className={`flex-1 h-2 rounded-full transition-colors ${s <= step ? 'bg-primary-500' : 'bg-gray-200'
              }`}
          />
        ))}
      </div>

      <div className="card">
        {/* Step 1: Personal Info */}
        {step === 1 && (
          <div className="space-y-6">
            <h2 className="text-lg font-semibold">{t('profile.step1')}</h2>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {t('profile.name')} *
              </label>
              <div className="flex gap-2">
                <input
                  type="text"
                  value={profile.name}
                  onChange={e => updateProfile('name', e.target.value)}
                  className="input-field flex-1"
                  placeholder={t('profile.name')}
                  aria-label={t('profile.name')}
                />
                <VoiceInput onResult={(text) => handleVoiceResult(text, 'name')} />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {t('profile.mobile')} *
              </label>
              <input
                type="tel"
                value={profile.mobile}
                onChange={e => updateProfile('mobile', e.target.value)}
                className="input-field"
                placeholder="9876543210"
                maxLength={10}
                aria-label={t('profile.mobile')}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {t('profile.state')} *
              </label>
              <select
                value={profile.state}
                onChange={e => updateProfile('state', e.target.value)}
                className="select-field"
                aria-label={t('profile.state')}
              >
                <option value="">Select State</option>
                {STATES.map(state => (
                  <option key={state} value={state}>{state}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {t('profile.district')} *
              </label>
              <input
                type="text"
                value={profile.district}
                onChange={e => updateProfile('district', e.target.value)}
                className="input-field"
                placeholder={t('profile.district')}
                aria-label={t('profile.district')}
              />
            </div>
          </div>
        )}

        {/* Step 2: Farm Details */}
        {step === 2 && (
          <div className="space-y-6">
            <h2 className="text-lg font-semibold">{t('profile.step2')}</h2>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {t('profile.landType')} *
              </label>
              <div className="grid grid-cols-3 gap-3">
                {(['irrigated', 'dry', 'mixed'] as const).map(type => (
                  <button
                    key={type}
                    type="button"
                    onClick={() => updateProfile('landType', type)}
                    className={`py-3 px-4 rounded-lg border text-sm font-medium transition-colors ${profile.landType === type
                        ? 'border-primary-500 bg-primary-50 text-primary-700'
                        : 'border-gray-300 hover:bg-gray-50'
                      }`}
                  >
                    {t(`profile.${type}`)}
                  </button>
                ))}
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {t('profile.acreage')} *
              </label>
              <input
                type="number"
                value={profile.acreage || ''}
                onChange={e => updateProfile('acreage', parseFloat(e.target.value) || 0)}
                className="input-field"
                placeholder="1.5"
                step="0.1"
                min="0"
                aria-label={t('profile.acreage')}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {t('profile.crops')} *
              </label>
              <div className="flex flex-wrap gap-2">
                {CROPS.map(crop => (
                  <button
                    key={crop}
                    type="button"
                    onClick={() => handleCropToggle(crop)}
                    className={`px-3 py-1.5 rounded-full text-sm transition-colors ${profile.mainCrops.includes(crop)
                        ? 'bg-primary-500 text-white'
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                      }`}
                  >
                    {crop}
                  </button>
                ))}
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {t('profile.farmerType')} *
              </label>
              <div className="grid grid-cols-3 gap-3">
                {(['owner', 'tenant', 'sharecropper'] as const).map(type => (
                  <button
                    key={type}
                    type="button"
                    onClick={() => updateProfile('farmerType', type)}
                    className={`py-3 px-4 rounded-lg border text-sm font-medium transition-colors ${profile.farmerType === type
                        ? 'border-primary-500 bg-primary-50 text-primary-700'
                        : 'border-gray-300 hover:bg-gray-50'
                      }`}
                  >
                    {t(`profile.${type}`)}
                  </button>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Step 3: Family & Income */}
        {step === 3 && (
          <div className="space-y-6">
            <h2 className="text-lg font-semibold">{t('profile.step3')}</h2>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {t('profile.familyCount')} *
              </label>
              <input
                type="number"
                value={profile.familyCount || ''}
                onChange={e => updateProfile('familyCount', parseInt(e.target.value) || 1)}
                className="input-field"
                placeholder="4"
                min="1"
                aria-label={t('profile.familyCount')}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                {t('profile.annualIncome')} *
              </label>
              <input
                type="number"
                value={profile.annualIncome || ''}
                onChange={e => updateProfile('annualIncome', parseInt(e.target.value) || 0)}
                className="input-field"
                placeholder="150000"
                min="0"
                step="1000"
                aria-label={t('profile.annualIncome')}
              />
              <p className="text-sm text-gray-500 mt-1">
                Approximately ‚Çπ{(profile.annualIncome || 0).toLocaleString('en-IN')} per year
              </p>
            </div>
          </div>
        )}

        {/* Navigation Buttons */}
        <div className="flex justify-between mt-8 pt-6 border-t">
          {step > 1 ? (
            <button
              onClick={handleBack}
              className="btn-secondary"
            >
              ‚Üê {t('profile.back')}
            </button>
          ) : (
            <div />
          )}

          {step < 3 ? (
            <button
              onClick={handleNext}
              className="btn-primary"
            >
              {t('profile.next')} ‚Üí
            </button>
          ) : (
            <button
              onClick={handleSubmit}
              disabled={isSubmitting}
              className="btn-primary"
            >
              {isSubmitting ? t('common.loading') : t('profile.submit')} üîç
            </button>
          )}
        </div>
      </div>

      {/* Privacy Note */}
      <p className="text-center text-sm text-gray-500 mt-6">
        üîê {t('profile.privacyNote')}
      </p>
    </div>
  );
};

export default ProfileWizard;
